const transposeMap = {'I': 0, 'ii': 2, 'iii': 4, 'IV': 5, 'V': 7, 'vi': 9, 'vii': 11, 'i': 0, 'iv': 5, 'v': 7, 'VI': 9, 'III': 4, 'VII': 11, 'bII': 1, 'bIII': 3, 'bVI': 8, 'bVII': 10};
const semitoneBase = {"C":0, "C#":1, "D":2, "D#":3, "E":4, "F":5, "F#":6, "G":7, "G#":8, "A":9, "A#":10, "B":11};
const notes = ['C', 'C#', 'D', 'D#', 'E', 'F', 'F#', 'G', 'G#', 'A', 'A#', 'B'];
const degreeQuality = {'I': '', 'ii': 'm', 'iii': 'm', 'IV': '', 'V': '', 'vi': 'm', 'vii': 'dim', 'i': 'm', 'iv': 'm', 'v': 'm', 'VI': '', 'III': '', 'VII': '', 'bII': '', 'bIII': '', 'bVI': '', 'bVII': ''};

function transpose(prog, key) {
    if (key === "") key = "C";
    let isMinor = key.endsWith("m");
    let root = key.replace("m","");
    let base = semitoneBase[root];
    if (isMinor) base = (base + 9) % 12;

    return prog.split(' - ').map(ch => {
        let degree = ch.replace(/[^a-zA-Z]/g, '');
        let suffix = ch.replace(/[a-zA-Z]/g, '');
        let offset = transposeMap[degree] ?? null;
        if (offset === null) return ch;
        let noteIndex = (base + offset) % 12;
        let note = notes[noteIndex];
        let quality = degreeQuality[degree] ?? "";
        return note + quality + suffix;
    }).join(' - ');
}

function generate() {
    const mood = document.getElementById('mood').value.toLowerCase();
    const genre = document.getElementById('genre').value.toLowerCase();
    const artist = document.getElementById('artist').value.toLowerCase();
    const key = document.getElementById('key').value;

    const filtered = data.filter(item => 
        (mood === '' || item.mood.toLowerCase() === mood) &&
        (genre === '' || item.genre.toLowerCase() === genre) &&
        (artist === '' || item.artist.toLowerCase() === artist)
    );

    let html = "";
    if (filtered.length === 0) {
        html = "<li>Aucune progression trouvée.</li>";
    } else {
        let selection = filtered.sort(() => 0.5 - Math.random()).slice(0, 20);
        selection.forEach(item => {
            const transposed = transpose(item.progression, key);
            html += `<li><b>${transposed}</b> — ${item.commentaire}</li>`;
        });
    }
    document.getElementById('results').innerHTML = html;
}
